mapper.select:
        SELECT p.`id`,p.`app_id`,p.`name`,p.`parent_id`,p.`code`,p.`description` 
		FROM `sys_user` u,`sys_role` r,sys_user_role ur,`sys_permission` p,`sys_role_permission` rp 
		WHERE u.`id`=ur.`user_id` AND ur.`role_id` = r.`id` AND rp.`role_id` = r.`id` AND rp.`permission_id` = p.`id` AND u.`user_name` = #{userName}

SELECT ep.id, ep.`name` 'name', ep.`code` 'code', ep.description, ep.STATUS 'status', (SELECT ep2.name FROM sys_permission ep2 WHERE ep2.id = ep.parent_id) parentName,ep.parent_id parentId,a.`name` appName
FROM sys_permission ep
LEFT JOIN sys_app a ON a.`id` = ep.`app_id`

    a.`status` = 0

        AND ep.`status`  = #{permission.status}


        AND ep.`name` = #{permission.name}


        AND ep.`app_id` = #{permission.appId}



    order by ep.${pager.sort} ${pager.order}
