mapper.select:
		SELECT r.`id`,r.`app_id`,r.`name`,r.`status`,r.`create_time`,r.`description`,r.`update_time` 
        FROM `sys_user_role` ur,`sys_user` u,`sys_role` r 
        WHERE u.`id` = ur.`user_id` AND r.`id` = ur.`role_id` AND u.`user_name` = #{userName}

SELECT r.id, r.app_id appId, r.name, r.status, r.description, r.create_time createTime, r.update_time updateTime,sa.`name` appName,sa.`status` appStatus,sa.`icon` appIcon,sa.`code` appCode
FROM sys_role r
LEFT JOIN sys_app sa ON sa.`id` = r.`app_id`



        AND r.`status` = #{role.status}


        AND r.`name` LIKE '%${role.name}%'


        AND r.`app_id` = #{role.appId}




    order by r.${pager.sort} ${pager.order}
